Export.Routine:
#include Globs #include %occStatus Start(ary,link) SET file = ##class(%Stream.FileCharacter).%New() SET blockWritten = 0 SET buffer="" set file.Filename=link set file.RemoveOnClose=0 set sock=##class(%IO.Socket).%New() set sock.TranslationTable="UTF8" do sock.Open(link,"7001",1,.sc) if ($$$ISOK(sc)) { set file=sock } set queryary=ary try {set TrashValue=@queryary} catch {set queryary = $QUERY(@queryary)} FOR {
QUIT:queryary="" set node = ##class(Globals.Utils).nodeToStr(queryary) set blockWritten = blockWritten + ##class(Globals.Utils).ChunkAndSend(node,.buffer, file) SET queryary=$QUERY(@queryary)
} do ##class(Globals.Utils).EncodeAndSave(buffer,file) set blockWritten = blockWritten+1 WRITE !,"Finished. Blocks written: ", blockWritten try {d file.%Save()} catch {} d file.%Close()
