Export.Routine:
#include Globs
#include %occStatus
#include %occMessages
Start(link)
	set timer = $ZHOROLOG
	set file = ##class(%Stream.FileCharacter).%New()
	set file.Filename=link
	set buf=""
	if (link="")
	{
		set sock = ##class(%IO.ServerSocket).%New() 
  		set sock.TranslationTable="UTF8"
  		do sock.Open("7001",1,.sc)
  		if $$$ISOK(sc) {

    	do sock.Listen()

    	do	{	
    		set s=sock.ReadAny($$$BLOCKSIZE, 5)
    		set s=##class(Globals.Utils).decode(s)
    		set buf=buf_s
    		while ($f(buf,$$$NL)>0) 
    			{
	    			do ##class(Globals.Utils).executeString($e(buf,1,$f(buf,$$$NL)-3))
	    			set buf=$e(buf,$f(buf,$$$NL),*)	    		
	    		}
    		}
    		while(s'="")

    		do sock.Close()
	}
	}
	else 
		{
		while ('file.AtEnd) {
			set s = file.Read($$$BLOCKSIZE)
			set s=##class(Globals.Utils).decode(s)
    		set buf=buf_s
    		while ($f(buf,$$$NL)>0) 
    			{
	    			do ##class(Globals.Utils).executeString($e(buf,1,$f(buf,$$$NL)-3))
	    			set buf=$e(buf,$f(buf,$$$NL),*)	    		
	    		}
			}
		}

	w "Successfully imported!"
